INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1110', '11', 'Nuclear Projectiles', NULLIF('Includes Ogive sections, body sections, bases, and other peculiar components which are not classifiable elsewhere.', ''), NULLIF('', ''), NULLIF('Note-This class includes nuclear weapons which are designed to be propelled from a recoilless rifle, gun, howitzer, or the like, and which are not designed to be self-propelled.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1115', '11', 'Nuclear Warheads and Warhead Sections', NULLIF('Includes Components and parts peculiar to the warhead or warhead section, which are not classifiable in more specific classes.', ''), NULLIF('Excludes Such components as fuzing and firing devices, nuclear components, high explosive components, classifiable in more specific classes, and items such as projectiles and bombs which include the entire outer case of a weapon.', ''), NULLIF('Note-This class includes nuclear warheads (without or assembled with case sections, adaption kits, and/or fuzing and firing components) which are to be used in or with bombs, rockets, projectiles, missiles, demolition charges, or the like. Also includes case sections, nose cones, flare sections, center sections, and auxiliary structural components of missiles, rockets, and re-entry vehicles which are designed or constructed for exclusive use with or for housing of nuclear warheads and/or warhead sections.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1120', '11', 'Nuclear Depth Charges', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes nuclear weapons which consist of depth charge bodies assembled with nuclear warheads. It includes training weapons and all specially designed components which are not classifiable elsewhere.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1125', '11', 'Nuclear Demolition Charges', NULLIF('Includes Outer cases, case sections, and other peculiar components, which are not classifiable elsewhere.', ''), NULLIF('', ''), NULLIF('Note-This class includes nuclear weapons which are designed to be emplaced in or near a structure, area, or the like, which is to be destroyed. The weapons may include accessories.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1127', '11', 'Nuclear Rockets', NULLIF('Includes Components peculiar to assembled rockets which are not classifiable elsewhere.', ''), NULLIF('Excludes Rocket Motors; Warhead Components; Rocket Components classified in other FSC classes.', ''), NULLIF('Note-This class includes nuclear weapons, consisting of rocket bodies assembled with nuclear warheads, which comprise a single item of supply and are issued as such.', ''));
