INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1145', '11', 'Explosive and Pyrotechnic Components, Nuclear Ordnance', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes actual and simulated explosive and pyrotechnic components peculiar to all nuclear ordnance end items, such as bombs, demolition charges, rockets, projectiles, warheads, and related systems. Excluded from this class are all explosive and pyrotechnic components designed for other than nuclear ordnance applications.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1190', '11', 'Specialized Test and Handling Equipment, Nuclear Ordnance', NULLIF('Includes Specially designed trucks and trailers for nuclear ordnance; Specially designed slings and hoists; Operational, test, and setting devices, and cradles; Conditioning Kits and Sets, Controlled Environment.', ''), NULLIF('Excludes Hand tools; Instruments for use on both nuclear ordnance and other equipment; Basic types of electrical and electronic test instruments, including those specially designed, such as ammeters, voltmeters, ohmmeters, multimeters, and similar instruments, as shown in the indexes in the FSC.', ''), NULLIF('', ''));
