INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1336', '13', 'Guided Missile Warheads and Explosive Components', NULLIF('Includes Warheads Containing Military Chemical Agents; Exercise Heads, Guided Missile.', ''), NULLIF('Excludes Atomic Ordnance Warheads; All other Warheads for use on other than guided missiles; Liquid Propulsion Units; Rocket Engines (Liquid Propellant Units); JATO Units; Rocket Motors (Solid Propellant Units) for Guided Missiles; Solid Fuel Propulsion Units; Propellant Chemicals encased in consumable containers, such as squibs and cartridges for insertion into missile propulsion systems as integral parts.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1337', '13', 'Guided Missile and Space Vehicle Explosive Propulsion Units, Solid Fuel; and Components', NULLIF('Includes Rocket Motors (Solid Propellant Units) for Guided Missiles, Explosive-Loaded; All Explosive Components for Guided Missile and Space Vehicle Solid Fuel Propulsion Units; Solid Propellants encased in consumable containers, such as squibs and cartridges, intended for insertion into missile propulsion systems as integral parts.', ''), NULLIF('Excludes Rocket Motors (Solid Propellant Units) for Guided Missiles, Inert-Loaded or Empty; All Inert (Nonexplosive) Components for Guided Missile and Space Vehicle Solid Fuel Propulsion Units; Guided Missile Warheads and Components; Rocket Propulsion Units; Rocket Motors (Solid Propellant Units) for Rockets; Solid Propellants predetermined to specific quantity and quality, but packaged in reusable containers.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1338', '13', 'Guided Missile and Space Vehicle Inert Propulsion Units, Solid Fuel; and Components', NULLIF('Includes Rocket Motors (Solid Propellant Units) for Guided Missiles, Inert-Loaded or Empty; All Inert (Nonexplosive) Components for Guided Missile and Space Vehicle Solid Fuel Propulsion Units.', ''), NULLIF('Excludes Rocket Motors (Solid Propellant Units) for Guided Missiles, Explosive-Loaded; Guided Missile Warheads and Components; Rocket Propulsion Units; Rocket Motors (Solid Propellant Units) for Rockets.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1340', '13', 'Rockets, Rocket Ammunition and Rocket Components', NULLIF('Includes Complete Rounds; Explosive Components, Including Warheads; Pyrotechnic Rockets; Solid Fuel JATO Units; Rocket Motors (Solid Propellant Units) for Rockets; Covers, Protective Rocket Warhead; Jettisonable Rocket Launchers; Conditioning Kits and Sets, Controlled Environment; and other peculiar components which are not classified elsewhere.', ''), NULLIF('Excludes Nuclear rockets, Nuclear warheads; all other warheads for use on other than rockets; and rockets with built-in guiding devices.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1345', '13', 'Land Mines', NULLIF('Includes Components; Antipersonnel Mines; Antitank Mines; Fuzes.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1346', '13', 'Remote Munitions', NULLIF('Includes: Remote Munition Items and Systems used singularly or in combination to defeat a variety of threats; Fuzes; Safe and Arming devises; Self Destruction Mechanisms; Communication Modules; Command and Control systems and various other pertinent components.', ''), NULLIF('', ''), NULLIF('Note-Munitions items/systems that are not held by the soldier but are deployed for future activation. They may be put in place separately by the soldier or delivered from vehicles or aircraft by mechanical distribution methods. They may be commdand controlled or may use self-initiated firing systems that do not require soldier monitoring. All items in this class can be armed/disarmed remotely.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1350', '13', 'Underwater Mine and Components, Inert', NULLIF('Includes Parachute Packs; Inert Mine Cases; Booster Cans; Clock Delays; Clock Starters; Search Coils; Extenders.', ''), NULLIF('', ''), NULLIF('', ''));
