INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1352', '13', 'Underwater Mine Disposal Inert Devices', NULLIF('Includes: Complete operating units, such as an underwater mine reconnaissance device, as well as component parts, practice, training, blank, and dummies.', ''), NULLIF('Excludes: Torpedo Inert components, depth charges, and underwater mines.', ''), NULLIF('Note-Version of Underwater Mine Disposal Explosive Device without destructive charge. Intended for underwater mine hunting and reconnaissance. May also be used for training purposes.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1353', '13', 'Underwater Mine Disposal Explosive Devices', NULLIF('Includes Complete explosive device, including ammunition, detonating charge, and component parts.', ''), NULLIF('Excludes Underwater Mine Explosive Components (FSC 1351), Torpedo Explosive Components (FSC 1356), Military Chemical Agents (FSC 1365).', ''), NULLIF('Note-Complete explosive devices, including destructive charge. It is fired, guided to target and detonated from a vessel or other remote site.', ''));
