INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1385', '13', 'Surface Use Explosive Ordnance Disposal Tools and Equipment', NULLIF('', ''), NULLIF('Excludes Nonspecialized or common tools and equipment not developed exclusively for use by qualified EOD personnel; specialized demolition material; explosive loaded shaped charges.', ''), NULLIF('Note-This class includes only specialized tools and equipment developed for and used by qualified Explosive Ordnance Disposal (EOD) personnel.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1386', '13', 'Underwater Use Explosive Ordnance Disposal and Swimmer Weapons Systems Tools and Equipment', NULLIF('', ''), NULLIF('Excludes Nonspecialized or common tools not developed exclusively for use by qualified EOD, UDT, and/or SEAL personnel; specialized demolition material; explosive loaded shaped charges', ''), NULLIF('Note-This class includes only specialized tools and equipment developed for and used by qualified Explosive Ordnance Disposal (EOD), personnel, Underwater Demolition Teams (UDT), and/or Sea-Air-Land (SEAL) personnel', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1390', '13', 'Fuzes and Primers', NULLIF('', ''), NULLIF('Excludes Fuzes and Primers for ammunition not classified in Classes 1310, 1315, and 1320.', ''), NULLIF('Note-This class includes only fuzes and primers for use in ammunition classified in Classes 1310, 1315, and 1320.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1395', '13', 'Miscellaneous Ammunition', NULLIF('Includes Ammunition Shapes, such as Blanks, Disks, Slugs, Cups, and Rotating Bands (Shell Bands).', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1398', '13', 'Specialized Ammunition Handling and Servicing Equipment', NULLIF('Includes Handling and servicing equipment specially designed for use on or with rockets and conventional ammunition.', ''), NULLIF('Excludes Handling and servicing equipment specially designed for use on or with rocket and pyrotechnic launchers; Ammunition maintenance and repair shop specialized equipment; Handling equipment specially designed for aircraft or airfield use.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1410', '14', 'Guided Missiles', NULLIF('Includes Complete Drones, initially designed as missiles, but converted to drone use. Also includes complete guided missile air launch decoys.', ''), NULLIF('', ''), NULLIF('Note-This class includes only complete guided missiles, with or without warheads and explosive components, whether in assembled or unassembled form. End items, assemblies, parts, attachments, or accessories for use in or on guided missiles are classified in classes other than this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1420', '14', 'Guided Missile Components', NULLIF('Includes Structural Components; Components and Accessories Specially Designed for use on or with guided missiles, including Complete Gyro Mechanisms, Hydraulic Pumps, Automatic Pilot Mechanisms and Specially Designed Assemblies, and Electronic Guidance Equipment installed in missiles, Mounting structure, Spare Kits, On Board Sensors, and Long Wave Infared Cameras.', ''), NULLIF('Excludes Electronic Remote Guidance Equipment used to guide missiles; Solid and Liquid Propellant Units; Components of Gyro Mechanisms.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1425', '14', 'Guided Missile Systems, Complete', NULLIF('', ''), NULLIF('Excludes Guided Missile Subsystems.', ''), NULLIF('Note-This class includes only complete guided missile systems. Anything less than a complete system, such as individual end items, assemblies, parts, attachments, or accessories for use in guided missile systems are classified in classes other than this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1427', '14', 'Guided Missile Subsystems', NULLIF('', ''), NULLIF('Excludes Complete Guided Missile Systems.', ''), NULLIF('Note-This class includes only combinations or assemblies of two or more end items, each of which is properly classified in other classes of this group, identified as single items of supply. Excluded from this class are individual end items, assemblies, subassemblies, attachments, accessories or parts classified in other classes of this group, not combined as single items of supply.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1430', '14', 'Guided Missile Remote Control Systems', NULLIF('Includes Specially Designed Components of Guided Missile Remote Control Systems.', ''), NULLIF('Excludes Guided Missile Internal (Built-in) Control Systems; Components used in both guided missiles and other than guided missiles (including fire control components).', ''), NULLIF('Note-This class includes airborne and nonairborne guided missile remote control systems, components, and accessories, designed specifically for use therewith. Excluded from this class are fire control assemblies, subassemblies, and components modified for use with guided missile remote control systems, and fire control assemblies designed specifically for shipboard use.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1440', '14', 'Launchers, Guided Missile', NULLIF('Includes Airborne and Nonairborne Guided Missile Launchers.', ''), NULLIF('Excludes Aircraft Launchers; Rocket Launchers.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1450', '14', 'Guided Missile Handling and Servicing Equipment', NULLIF('Includes Specially Designed Trucks and Trailers for use in transporting guided missiles; Specially Designed Slings, Hoists, Jacks, and Blowers; Self-propelled Vehicles and Trailers, Specially Designed for Guided Missile Handling or Servicing; Covers, Guided Missile; Conditioning Kits and Sets, Controlled Environment.', ''), NULLIF('Excludes Guided Missile Launchers (FSC 1440); Aircraft Handling and Servicing Equipment (FSC 1730).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1510', '15', 'Aircraft, Fixed Wing', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes only complete aircraft. End items, assemblies, parts, attachments, or accessories for use in or on fixed wing aircraft are classified in classes other than this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1520', '15', 'Aircraft, Rotary Wing', NULLIF('Includes Helicopters.', ''), NULLIF('', ''), NULLIF('Note-This class includes only complete aircraft. End items, assemblies, parts, attachments, or accessories for use in or on rotary wing aircraft are classified in classes other than this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1540', '15', 'Gliders', NULLIF('Includes Motorized Gliders.', ''), NULLIF('', ''), NULLIF('Note-This class includes only complete gliders. End items, assemblies, parts, attachments, or accessories for use in or on gliders are classified in classes other than this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1550', '15', 'Unmanned Aircraft', NULLIF('Includes Unmanned Aircraft Systems (UASs) and Drones specifically designed for such uses as targets, training, surveillance, missile evaluation, photographic reconnaissance, weapons delivery, communications relay/network gateway, electronic warfare, search and rescue, re-supply or other military operateons.', ''), NULLIF('Excludes Converted or modified guided missiles.', ''), NULLIF('Note-This class includes only complete unmanned aircraft systems and subordinate air vehicles. Other major items, assemblies, parts, attachments, or accessories for use in or on unmanned aircraft systems are classified in classes other than this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1555', '15', 'Space Vehicles', NULLIF('', ''), NULLIF('', ''), NULLIF('Note - This class includes only complete space vehicles, whether in assembled or unassembled form.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1560', '15', 'Airframe Structural Components', NULLIF('Includes Flight Control Surfaces; Internal and External Auxiliary Fuel Tanks; Exhaust Systems; Pylons, Trim Tabs; Aircraft.', ''), NULLIF('Excludes Fitted Covers; Helicopter Rotor Brake System Components; Aircraft Loose Equipment and Alternate Mission Configuration Equipment stored on board the aircraft.', ''), NULLIF('Note-This class includes fabricated system parts that are permanently attached or peculiar to the integral airframe of an aircraft, such as support structural components, spars, ribs, ailerons, stabilizers, bulkheads.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1610', '16', 'Aircraft Propellers and Components', NULLIF('Includes Aircraft Propellers; Propeller Blades, Cams, Cones, Hubs, Nuts, and Spinners; Test Clubs; Synchronizers; Power Control Units; Integral Oil Control Measures and Propeller Governors.', ''), NULLIF('Excludes Rotary Rudder and Rotary Wing Blades (FSC 1615).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1615', '16', 'Helicopter Rotor Blades, Drive Mechanisms and Components', NULLIF('Includes Helicopter dynamic components and specially designed parts that transmit power from the aircraft power plant to the rotary wing and rotary rudder. Also included in this class are Rotors; Blades; Rotor Blade, Trim, Tabs; Blade Sets; Yokes; Clutches and Transmissions.', ''), NULLIF('Excludes Propellers (FSC 1610); Rotor Brake Systems Components (FSC 1630); Rotor Blade Hydraulic Folding System Components (FSC 1650); Hydraulic Servo System Components (FSC 1650).', ''), NULLIF('Note-This class includes miscellaneous component parts specifically designed for, and used exclusively in, helicopter drive mechanisms and rotor blades when not specifically classified elsewhere in the FSC indexes.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1620', '16', 'Aircraft Landing Gear Components', NULLIF('Includes Shock Struts and Components; Installation Elements, such as Torsion Bars, Vibration Links, Drag Struts; Landing Gear Trunions, Axles and Shimmy Dampeners; Specially designed hydraulic power steering system components.', ''), NULLIF('Excludes Mounting Braces and Mounting Plates permanently installed on aircraft (FSC 1680); Landing Wheels, Skis, and Floats (FSC 1630); Wheel Brakes and Wheel Brake Cylinders (FSC 1630).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1630', '16', 'Aircraft Wheel and Brake Systems', NULLIF('Includes Skis; Floats; Tracks; Landing Wheel Skid Detectors; Valves specifically designed for use with hydraulic or pneumatic wheel and brake systems; Helicopter Rotor Brake System Components.', ''), NULLIF('Excludes Landing Gear Axles (FSC 1620).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1640', '16', 'Aircraft Control Cable Products', NULLIF('Includes Wire Rope; Single Leg Wire Assemblies; Wire Strands; Control Pulleys; Turnbuckle Lock Clips and other wire rope attachments and terminations.', ''), NULLIF('Excludes General use Chain and Wire Rope (FSC 4010); general use Pulleys (FSC 3020); general use Miscellaneous Hardware (FSC 5340); general use Fittings for Rope, Cable and Chain (FSC 4030).', ''), NULLIF('Note-Wire rope, with attachments or terminations and pulleys, used in aircraft control applications, will be classified in this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1650', '16', 'Aircraft Hydraulic, Vacuum, and De-icing System Components', NULLIF('Includes Hydraulic and Pneumatic Accumulators, Pumps, Motors, Actuating Cylinders, and Filters; De-icing Boots; Fluid Type De-icing Pumps, Valves and Filters; Vacuum System Oil Separators; Pneumatic Pressurization Equipment other than that for pressurizing cabins and compartments.', ''), NULLIF('Excludes Jet Engine Hydraulic Components (FSC 2840); Valves specifically designed for air conditioning, heating, ventilating, cabin pressurizing, and thermal de-icing (FSC 1660); Valves specifically designed for use with Aircraft Engines (FSC 2840); Guided Missiles and Guided Missile Engines (FSC 1338), Landing Gear (FSC 1620), Wheel and Brake System Components (FSC 1630); Oxygen Breathing System Components (FSC 1660); In-flight Refueling System Hydraulic components (FSC 1680); Specifically designed hydraulic power steering system components (FSC 1620).', ''), NULLIF('Note-This class includes only those components specifically designed for aircraft use.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1660', '16', 'Aircraft Air Conditioning, Heating, and Pressurizing Equipment', NULLIF('Includes Cabin Supercharging Equipment; Canisters; Cylinder Assemblies; Masks; Fixed Oxygen System; Specially Designed Aircraft Valves; Cabin Pressure Regulators; Heat Exchangers; Air Expansion Turbines; Aircraft Heaters; Ventilating System Components; Air Conditioning and Heating Duct Assemblies; Thermal De-icing Equipment; Cabin and Compartment Pressurizing Equipment; Air Diffusers; Cabin Pressure Selectors; Liquid Oxygen Converters.', ''), NULLIF('Excludes Oxygen Pressure Signals and Warning Devices; Tubing; Ground Type Heaters and Coolers; Pressurizing Equipment other than that used for Cabin and Compartment Pressurization; Fluid De-icing Equipment; Valves specifically designed for use with Aircraft Engines, Guided Missiles, and Guided Missile Engines.', ''), NULLIF('Note-This class includes components specifically designed for use in aircraft air conditioning, heating, and pressurizing equipment. Also included are specially designed components of oxygen breathing systems used in aircraft.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1670', '16', 'Parachutes; Aerial Pick Up, Delivery, Recovery Systems; and Cargo Tie Down Equipment', NULLIF('', ''), NULLIF('Excludes Fitting Assemblies built into the aircraft structure.', ''), NULLIF('Note-Includes specifically designed items, sets, and systems for air-to-air, air-to-surface, and surface-to-air delivery, pick up, and recovery operations, unless parts, attachments, assemblies, for use in or on such systems (i.e., space vehicle aerial recovery systems) are specifically indexed to other classes of the FSC (i.e., Transmitting Radio Buoys and Direction Finding Subsystem Components).', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1675', '16', 'Space Vehicle Components', NULLIF('Includes Structural Components; Components and Accessories specially designed for installation in or on Space Vehicles; Internal (Built-in) Control Systems.', ''), NULLIF('Excludes Remote Guidance Equipment.', ''), NULLIF('', ''));
