INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1680', '16', 'Miscellaneous Aircraft Accessories and Components', NULLIF('Includes Control Assemblies, Push-Pull; Brace, Positioning Cargo Ramp stowed on board; Cockpit Mounted Control Quadrants; Actuators, Electro-Mechanical and Mechanical; Ventilators; Relief Tubes; Map Holders; Aerial Glider Towing Accessories attached to Aircraft; Belts, Safety and Lap; Harness, Shoulder and Safety; Litter Attaching Supports;Electric Windshield Wipers;Aircraft Onboard Inert Gas Generators;Aircraft Furniture;Aircraft Mounted Winches and Hoists;In-Flight Refueling System Components, including Fuel Components;Aircraft Curtains;Cable Tension Regulators;Sun Visors;Rear-View Mirrors;Mechanical Transmissions, Gearboxes and Constant Speed drives Specially designed for aircraft.', ''), NULLIF('Excludes Engine Mounted Control Assemblies; Fitted Covers, Aircraft Pylons; Helicopter Roter Brake System Components; Helicopter Automatic Stabilization Equipment.', ''), NULLIF('', ''));
