INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('1735', '17', 'Space Vehicle Handling and Servicing Equipment', NULLIF('Includes Specially designed Trucks and Trailers for use in transporting Space Vehicles; Specially designed Slings, Hoists, Jacks, and Blowers, Self-propelled Vehicles, specially designed for space vehicle handling or servicing; Covers, space vehicle.', ''), NULLIF('Excludes Equipment used in handling or servicing both Guided Missile and Space Vehicles.', ''), NULLIF('Note - This class does not include space vehicle aerial recovery systems which are classified in Class 1670.', ''));
