INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('2320', '23', 'Trucks and Truck Tractors, Wheeled', NULLIF('Includes Panel, Delivery and Pick Up Trucks, Military Cargo Carrying Vehicles, including Wheel Mounted Amphibian Vehicles; Truck Tractors and Trailer Combinations.', ''), NULLIF('Excludes Fire Trucks; Special Construction Type Earth and Rock Hauling Trucks; Motorized Air Compressors; Motorized Concrete Mixers; Construction Specialized Machinery Generally; Specially designed trucks for use in and around airfields, hangers, and warehouses; Vehicles specifically designed and/or modified for combat, assault, and tactical situations. Tracked and Halftracked Vehicles. Wheeled Self-Propelled Combat, Assault, and Tactical Vehicles with or without amphibian capabilities; Armored Wheeled Vehicles; Mine Resistant Ambush Protected Vehicles (2355); Self-Propelled Wheeled Sleds; and Self-Propelled Wheeled Flame Throwers.', ''), NULLIF('Note-This class includes only complete wheel mounted trucks and truck tractors, and chassis therefor. Any end items, assemblies, parts, attachments, or accessories other than complete chassis, for use in or on such trucks or truck tractors are classified in classes other than this class. The combined chassis and body of a special purpose truck, such as a machine shop, mobile laundry, or dental laboratory, is classified in this class. The complete mobile unit, consisting of chassis, body, and additional equipment, as in an equipped truck mounted machine shop, is excluded from this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('2330', '23', 'Trailers', NULLIF('Includes Semitrailers; Semitrailer Dollies; Cargo Trailers; Passenger and House Trailers; Special Purpose Trailers, such as Ammunition.', ''), NULLIF('Excludes Fire Fighting Trailers; Specially designed trailers for use in and around airfields, hangers, and warehouses.', ''), NULLIF('Note-This class includes only complete trailers, and chassis therefor. Any end items, assemblies, parts, attachments, or accessories, other than complete chassis, for use in or on trailers are classified in classes other than this class. The combined chassis and body of a special purpose trailer, such as a machine shop, mobile laundry, or dental laboratory, is classified in this class. The complete mobile unit, consisting of chassis, body, and additional equipment, as in an equipped trailermounted machine shop, is excluded from this class.', ''));
