INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('3612', '36', 'Three Dimensional Printers and Scanners', NULLIF('Includes - Fused Filament Fabrication (FFF); Stereolithography (SLA); Digital Light Processing (DLP); Selective Laser Sintering (SLS); Selective laser melting (SLM); Direct Metal Laser Melting (DMLM) or Laser Powder Bed Fusion (LPBF); Multi Jet Fusion (MJF); Poly Jet; Direct Metal Laser Sintering (DMLS); Electron Beam Melting/ Digital Beam Melting (EBM) and Laminated object manufacturing (LOM) type printers; Short Range; Medium Range; Long Range; Laser Based; Projected Light; Structured Light; Coordinate Measuring Machine (CMM); Arm Based Probe Systems; and Optical Tracked Probe System Scanners.', ''), NULLIF('Exclusion - Excludes Laser; Solid Ink; Light-Emitting Diode (LED); Inkjet; Multifunction, Dot Matrix Printers; Offset Presses; Typesetting Machinery; Photoengraving Machinery; Gelatine Process Machines; Spirit Process Machines; Stencil Process Machines; Offset Process Machines; Photostat Machines; Blueprint Printing and Developing Machines; Microfilm Machines; Marking Machines; Label Printing Machines; Label Printing Presses; and Embossing Machines. Computerized Tomography (CT) scanners; Positron Emission Tomography (PET) scanners; Ultrasound and Echocardiogram scanners.', ''), NULLIF('Note - This FSC includes all classes and sizes of three-dimensional printers and scanners. Additive manufacturing is a process that manufactures three-dimensional objects from three-dimensional model data by joining materials together, usually layer upon layer, as opposed to subtractive manufacturing methodologies.', ''));
