INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('3915', '39', 'Materials Feeders', NULLIF('Includes Dry Materials Feeders; Parts Feeders', ''), NULLIF('Excludes Aggregate and Fines Feeders; Conveyor Feeders; Water Purification Dry Chemical Feeders.', ''), NULLIF('Note-Materials Handling equipment included in this class is restricted to those materials feeders and parts feeders that serve as a conveying device, and by which the rate of delivery of bulk materials, packages, or objects may be controlled. Items included in this class are synchronized with companion machinery, feeding predetermined amounts, by volume, of specific materials accurately and continuesly from storage bins or hoppers to process or mixing machines, and are interchangable between machines. Excluded from this class are conveyor feeders (i.e., apron, belt, chain, flight, pan, etc., adapted for feeder service) that are classifiable in Class 3910 and/or Class 3895, as appropriate.', ''));
