INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4010', '40', 'Chain and Wire Rope', NULLIF('Includes Bead Chain; Weldless Chain; Single Leg Chain Assemblies; Wire Rope; Wire Cord; Single Leg Wire Assemblies.', ''), NULLIF('Excludes Power Transmission Chain (FSC 3020); Slings.', ''), NULLIF('Note-Chain and wire rope, with attachments or terminations will be classified in this class when they are for general use. a. Chain is a series of flexible links and/or rings fitted into one another used for supporting, lifting,restraining, dragging, or transmitting power. b. Wire rope is formed from strands of twisted wire.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4020', '40', 'Fiber Rope, Cordage, and Twine', NULLIF('Includes Fibrous Cord Assemblies; Fibrous Cord; Fibrous Rope; Fibrous Twine.', ''), NULLIF('Excludes Wire Rope (FSC 4010); Slings (FSC 3940).', ''), NULLIF('Note-Fiber rope is classified in this class when it is bulk material or for general use. Items with attachments and/or terminations are identified in this class unless a more specific item name is used. a. Fiber rope is flexible twisted strands of manila, nylon or other fiber. b. Cordage is ropes or cords used in rigging (see item name definitions for parameters). c. Twine is two or more strands twisted together.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4030', '40', 'Fittings for Rope, Cable, and Chain', NULLIF('Includes Anchor Guys; Wire Stranded Bands; Wire Rope Clamps; Cargo Hooks; Shackles; Swivels; Rope Terminals; Rope Wedges.', ''), NULLIF('Excludes Clamp Blocks (FSC 5340); Support Hooks (FSC 5340).', ''), NULLIF('Note-All items, including specially designed, will be classified in this FSC. Fittings classified in this FSC are items used in conjunction with chain, rope, and are attached to perform certain functions, (e.g., hooks, shackles, and wedges) refer to item name definitions for parameters.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4110', '41', 'Refrigeration Equipment', NULLIF('Includes Grocery Type Display Cases; Ice Cream Cabinets; Drinking Fountains (except Portable and stationary types classified in Class 4510); Professional and Scientific Refrigerators; Rivet Coolers; Photographic Refrigerators; Mortuary Refrigerators; Household and Commercial Refrigerators; Grocery Type Showcases; and other types of refrigeration equipment of the commissary or food store type.', ''), NULLIF('Excludes Cafeteria Type Refrigerated Display Cases; Soda Fountains; Milk, Soda, and/or Ice Cream Dispensers; Cold Food Counters; Carbonators; Combination Heated-Refrigerated Food Serving Equipment; Cafeteria Type Showcases; Chilling Equipment Used for Display and Serving of Food (see Class 7310 for these items). Also excluded from this class are refrigeration components (see Class 4130).', ''), NULLIF('Note-This class includes only complete refrigeration equipment, whether refrigerated from an integral or remote mechanical refrigeration unit or system. Also included in this class are refrigeration plants, and complete "packaged" refrigeration units and systems containing a power driven compressor, condenser, evaporator, and all other necessary components to refrigerate an enclosed space. Nonoperating accessories such as ice trays, shelves, and panels are included in this class. Excluded from this class are operating components, assemblies, parts, attachments, or accessories, for use in or on complete refrigeration equipment, plants, systems, or the "packaged" refrigeration units, and which are to be classified in Class 4130, or as otherwise specifically prescribed by the indexes and structure of the FSC.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4120', '41', 'Air Conditioning Equipment', NULLIF('Includes Store Type Units; Window and Room Type Units; Marine, and Vehicular Air Conditioning Units; Air Conditioning Plants and Systems.', ''), NULLIF('Excludes Air Conditioning Components (FSC 4130, or as otherwise specifically prescribed by the indexes and structure of the FSC).', ''), NULLIF('Note-This class includes only complete air conditioning equipment, systems, and plants. Also included are specially designed nonoperating accessories such as a window panel used with a window type air conditioning unit to facilitate mounting. Excluded from this class are operating components, assemblies, parts, attachments, or accessories for use in or on air conditioning equipment, which are to be classified in Class 4130, or as otherwise specifically prescribed by the indexes and structure of the FSC.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4130', '41', 'Refrigeration and Air Conditioning Components', NULLIF('Includes Heat Exchanger Equipment; Refrigerant Strainers; Refrigeration Compressors; Air Conditioning and Refrigeration Servicing Equipment, such as Charging and testing manifolds, Lines and Units; Operating Components of Refrigeration and Air Conditioning Equipment, Plants, and Systems classified in classes 4110 and 4120.', ''), NULLIF('Excludes Refrigeration and Air Conditioning Plants (see classes 4110 and 4120); Collections of Refrigeration Components packaged as a unit including a refrigeration compressor, condenser, evaporator, and the like specifically designed and tailored for applications like walk-in type refrigerators. Individual components of the packaged unit will be classified in class 4130 or as otherwise specifically prescribed by the indexes and structure of the FSC. The complete package unit will be classified in class 4110.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4140', '41', 'Fans, Air Circulators, and Blower Equipment', NULLIF('Includes Wall Type Fans; Pedestal Type Fans; Household Type and Attic Type Exhaust Fans; Floor Fans; Forged Draft Exhaust and Disposal Kits; Fans, Air Circulators, and Blower Equipment, whether designated as "industrial" or "nonindustrial" by application and/or use.', ''), NULLIF('Excludes Combination Heater-Fans (FSC 4520).', ''), NULLIF('Note-Fans and impellers which are specifically designed for use with specific individual types of equipment are to be classified in the same classes as their next higher assemblies or in the appropriate components or accessories classes (as in Group 25 or 29 or Class 1680) as prescribed by the indexes and structure of the FSC.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4150', '41', 'Vortex Tubes and Other Related Cooling Tubes', NULLIF('Includes Tool Grinding spot coolers; machining spot coolers; electronic equipment spot coolers; and electronic cabinet spot coolers.', ''), NULLIF('Excludes Refrigeration and Air Conditioning Plants (Classes 4110 and 4120); Air Conditioning Components and Cooler Unit, Air (Class 4130); Cooler, Air, Evaporative (Class 4120) and Pipe and Tube (Group 47).', ''), NULLIF('', ''));
