INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4710', '47', 'Pipe, Tube and Rigid Tubing', NULLIF('Includes Culvert Pipes; Culvert Pipe Connector Bands; Metallic Pipes; Plastic Pipes; Tubes and Rigid Tubing and their assemblies.', ''), NULLIF('Excludes Air Conditioning-Heating Pipes (FSC 4520); Asbestos and Cement, Bituminized Fiber, Clay and Concrete Pipes (FSC 5630); Conduit Pipes and their assemblies (FSC 5975); Downspout Pipes (FSC 5670); Exhaust Pipes (FSC 1560, 2990, and  4520); Fire Extinguishing Systems Pipes and Tubes and their assemblies (FSC 4210); Flexible Tubing (FSC 4720); Glass Tubing (FSC 6640, and 9340); Laboratory Tubes (FSC 6640); Medical and Surgical Tubes (FSC 6515).', ''), NULLIF('', ''));
