INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4730', '47', 'Hose, Pipe, Tube, Lubrication, and Railing Fittings', NULLIF('Includes Adapters; Bends; Caps; Clamps; Connectors; Couplings; Crosses; Elbows; Expansion Joints; Ferrules; Flanges; Laterals; Lubrication Fittings; Manifolds; Nipples; Nozzles; Outlets; Plugs; Reducers; Swing and Swivel Joints; Tees; Traps; Unions; Wyes.', ''), NULLIF('Excludes Air Conditioning-Heating Fittings (FSC 4520); Bituminized Fiber and Concrete Fittings (FSC 5630); Conduit Boxes and Fittings (FSC 5975); Downspout Fittings (FSC 5670); Medical and Surgical Fittings (FSC 6515); Laboratory Fittings (FSC 6640); Post Caps and Rail Ends (FSC 5660); Machine Thread Bushings and Plugs (FSC 5365).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4810', '48', 'Valves, Powered', NULLIF('Includes Electric Motor Operated Valves; Hydraulic Operated Valves; Solenoid Operated Valves.', ''), NULLIF('Excludes Engine Intake and Exhaust valves (FSC 2990, 2815); Valves specifically designed for use with fire fighting equipment (FSC 4210); plumbing equipment, and the like (FSC 4540).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4820', '48', 'Valves, Nonpowered', NULLIF('Includes Automatic Nonpowered Valves; Gate, Globe, Angle, Check, and Relief Valves; Cocks.', ''), NULLIF('Excludes Fire Hydrants (FSC 4210); Sprinkler Heads (FSC 4210); Valves specifically designed for use with fire fighting Equipment (FSC 4210); plumbing equipment, and the like (FSC 4540).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4910', '49', 'Motor Vehicle Maintenance and Repair Shop Specialized Equipment', NULLIF('Includes Automotive Lifts; Wheel Aligners; Brake Service Equipment; Tire Maintenance and Repair Equipment; Test stands and test equipment specially designed for use with motor vehicles.', ''), NULLIF('Excludes Hand Tools (FSG 51); Lubrication and Fuel Dispensing Equipment (FSC 4930); Basic types of electrical and electronic test instruments, including those specifically designed, such as ammeters, voltmeters, ohmmeters, multimeters, and similar instruments as shown in the indexes in the FSC (FSC 6625).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4915', '49', 'SHIP MAINTENANCE AND REPAIR SHOP SPECIALIZED EQUIPMENT', NULLIF('Includes: Maintenance platforms used during repair or overhaul; Test Stands and Test Equipment specially designed for maintenance and repair of ship components such as: engines, outboard motors, propulsion shafts, propellers, steering gears, rudders and sails.', ''), NULLIF('Excludes: Hand Tools (FSG 51); Basic types of electrical and electronic test instruments, including those specifically designed, such as ammeters, voltmeters, ohmmeters, multimeters, and similar instruments (See FSC 6625).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4920', '49', 'Aircraft Maintenance and Repair Shop Specialized Equipment', NULLIF('Includes Maintenance stands designed for support of aircraft assemblies during repair or overhaul; Test Stands and Test Equipment specially designed for maintenance and repair of aircraft components such as: engines, generators, hydraulic systems, armament, automatic pilot, fire control, flight control, and navigational systems.', ''), NULLIF('Excludes Hand Tools; Airfield Maintenance Platforms; Basic types of electrical and electronic test instruments, including those specially designed, such as ammeters, voltmeters ohmmeters, multimeters, and similar instruments, as shown in the indexes to the FSC; Test Apparatus used for both communications and other electrical and electronic equipment.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4921', '49', 'Torpedo Maintenance, Repair, and Checkout Specialized Equipment', NULLIF('Includes Specially designed maintenance, test, checkout, and repair shop specialized equipment for maintenance and repair of torpedoes, torpedo components; adapters, fixtures, inspection and holding fixtures, leveling jack assemblies, fuel filling and syphon assemblies, control surface adapter and protractor assemblies, afterbody cradle adapter and tilting mount assemblies, pressure air heater assemblies, motor drier and puller assemblies, test stand levels and stands for overhaul, maintenance, test, checkout, and repair of torpedo and torpedo components, test panels, fixtures, and test sets for electrical circuits, firing circuits and torpedo test sets.', ''), NULLIF('Excludes Specially designed maintenance test, checkout and repair shop specialized equipment for maintenance and repair of ammunition, guided missiles, rockets, fire control, space vehicles, underwater mines, depth charges and nuclear ordnance. Also excludes "basic" types of electrical and electronic test instruments, such as ammeters, voltmeters, ohmmeters, multimeters, galvanometers and similar instruments.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4923', '49', 'Depth Charges and Underwater Mines Maintenance, Repair, and Checkout Specialized Equipment', NULLIF('Includes Specially designed maintenance, test, checkout and repair shop specialized equipment for maintenance and repair of depth charges and underwater mines; adapters, inspection, test and holding fixtures; stands for overhaul, maintenance, test, checkout, and repair of underwater mines and depth charges; test fixtures, panels plus assemblies and test sets for leak test, pressure test, firing circuits and timing devices.', ''), NULLIF('Excludes Specially designed maintenance, test, checkout, and repair shop equipment designed for nuclear ordnance, guided missiles, ammunition, torpedoes, rockets, fire control, space vehicles and "basic" types of electrical and electronic test instruments.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4925', '49', 'Ammunition Maintenance, Repair, and Checkout Specialized Equipment', NULLIF('Includes Specially designed maintenance, test, checkout, and repair shop specialized equipment, for maintenance and repair of ammunition items; adapters, ammunition feeders and hoppers; inspection and holding fixtures; linkers, linkers-delinkers, and delinkers to assemble and disassemble ammunition belts; surveillance ovens; explosimeters; primer firing device fixtures; mandrels; repositioning machines; gas bomb service kits; test fixtures; panels; plug assemblies; and test sets for ammunition maintenance, checkout and repair.', ''), NULLIF('Excludes Maintenance, repair, checkout and test equipment specially designed for use with guided missiles, guided missile remote control systems, guided missile systems; fire control; nuclear ordnance; rockets, underwater mines and depth charges; torpedoes; space vehicles and test equipment for aircraft weapon launchers; basic types of electrical and electronic test instruments, such as ammeters, voltmeters, ohmmeters, multimeters, and similar instruments, as shown in the indexes to the FSC.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4927', '49', 'Rocket Maintenance, Repair and Checkout Specialized Equipment', NULLIF('Includes Specially designed maintenance, test, checkout, and repair shop specialized equipment for maintenance and repair of rockets and rocket components; rocket maintenance stands; test stands; fixtures; jigs; and shop equipment designed for maintenance and repair of rockets, rocket motors and other rocket components.', ''), NULLIF('Excludes Specially designed maintenance, test, checkout, and repair shop equipment specifically designed for nuclear ordnance, guided missiles, fire control, torpedoes, underwater mines, depth charges, ammunition, space vehicles; test equipment for aircraft rocket launchers and "basic" types of electrical and electronic test instruments, such as ammeters, voltmeters, ohmmeters, multimeters, galvanometers and similar instruments.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4930', '49', 'Lubrication and Fuel Dispensing Equipment', NULLIF('Includes Hand Grease Guns; Centralized Lubrication Systems; Hydrostatic Lubricators; Oil and Gasoline Dispensing Pumps; Fuel Oil Dispensing Pumps; Hand Oilers; Grease Dispensers; Pressure Gun Attachments; Sight Feed Lubricators.', ''), NULLIF('Excludes Alemite and Lubrication Fittings (FSC 4730).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4931', '49', 'Fire Control Maintenance and Repair Shop Specialized Equipment', NULLIF('Includes Specially Designed Test Equipment, Maintenance Fixtures and Stands.', ''), NULLIF('Excludes Instruments for use on both Fire Control and other Equipment; Airborne Fire Control Maintenance and Repair Shop Equipment.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4933', '49', 'Weapons Maintenance and Repair Shop Specialized Equipment', NULLIF('Includes Maintenance Stands, Fixtures, and Jigs.', ''), NULLIF('Excludes Atomic Ordnance Maintenance and Repair Shop Specialized Equipment; Airborne Weapons Maintenance and Repair Shop Equipment.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4935', '49', 'Guided Missile Maintenance, Repair, and Checkout Specialized Equipment', NULLIF('Includes Checkout equipment and test equipment specially designed for use with guided missiles and guided missile remote control systems.', ''), NULLIF('Excludes Specially designed internal (built-in) checkout equipment for guided missile remote control systems; Basic types of electrical and electronic test instruments, including those specially designed, such as ammeters, voltmeters, ohmmeters, multimeters, and similar instruments, as shown in the indexes to the FSC; Test Apparatus used for both communications and other electrical and electronic equipment.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4940', '49', 'Miscellaneous Maintenance and Repair Shop Specialized Equipment', NULLIF('Includes Paint Spraying Equipment.', ''), NULLIF('Excludes Hand Tools (FSG 51).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4960', '49', 'Space Vehicle Maintenance, Repair, and Checkout Specialized Equipment', NULLIF('Includes Checkout and Test Equipment specially designed for use with Space Vehicles, including Remote Control Systems.', ''), NULLIF('Excludes Checkout and Test Equipment used with both Guided Missiles and Space Vehicles; Specially designed Internal (Built-in) Checkout Equipment for Remote Control Systems; Basic types of Electrical and Electronic Test Instruments, including those specially designed, such as ammeters, ohmmeters, multimeters, and similar instruments, as shown in the indexes to the FSC.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('4970', '49', 'Multiple Guided Weapons, Specialized Maintenance and Repair Shop Equipment', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Maintenance, test, checkout and repair shop equipment designed to test, maintain, fault isolate and repair multiple guided weapon systems. For equipment designed to be used with only one specific system see the individual FSC indexed (i.e., 4921, 4927, 4935).', ''));
