INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5120', '51', 'Hand Tools, Nonedged, Nonpowered', NULLIF('Includes Hammers; Picks; Pliers, except pliers for cutting only; Screwdrivers; Shovels; Construction Rakes, Forks and Hoes; Jacks, including Contractors'' Jacks; Wrecking Bars; Glue Pots; Blowtorches.', ''), NULLIF('Excludes Craftsman''s Measuring Tools; Gardening Rakes, Forks, Hoes, and other Garden Tools.', ''), NULLIF('', ''));
