INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5180', '51', 'Sets, Kits, and Outfits of Hand Tools', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes sets, kits and outfits consisting of several different items classifiable either in a single class or in several classes. Excluded from this class are sets, kits and outfits consisting of variations (such as size or color) of an item. Classify these items in the same class as the individual item.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5210', '52', 'Measuring Tools, Craftsmen''s', NULLIF('Includes Calipers; Levels; Micrometers; Plumb Bobs; Precision Tapes; Squares; Angle Gages; Center Gages; Depth Gages; Draw Gages; Drill Point Gages; Fillet and Radius Gages; Glaziers'' Gages; Height Gages (Vernier); Planer Gages; Rivet Selector Gages; Saw Tooth Set Gages; Screw Pitch Gages; Surface Gages; Telescoping Gages; Thickness Gages; Tube Bead Gages; Tube Flare Gages; Twist Drill Gages; Twist Drill and Rod Gages; Twist Drill and Tap Gages; Taper-Wire-Thickness Gages; Wire Gages; Tool Setting Planer and Shaper Gages; Gage Blocks.', ''), NULLIF('', ''), NULLIF('Note-This class does not include special inspection gages, which are classified in Class 5220.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5220', '52', 'Inspection Gages and Precision Layout Tools', NULLIF('Includes Go and Not-Go Gages, including Plug, Ring, Snap, Thread, and Length Gages; Profile Gages; Fixture Gages; Special Inspection Gages.', ''), NULLIF('', ''), NULLIF('Note-Special inspection gages are included in this class.', ''));
