INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5310', '53', 'Nuts and Washers', NULLIF('Includes Locknuts; Eye Nuts; Plain Nuts; Castellated Nuts; Wing Nuts; Plate Nuts; Push on Nuts; Concave and Convex Washers; Flat Washers; Keyed Washers; Lock Washers; Shouldered Washers; Split Washers.', ''), NULLIF('Excludes Bearing Thrust Washers (FSC 3120); Shims (FSC 5365); Ring Spacers (FSC 5365); Plate Spacers (FSC 5365); Lock Rings (FSC 5365).', ''), NULLIF('Note-Nuts and washers, including specially designed, are to be classified in this FSC. a. Nuts are a small metal block made in a variety of shapes such as square and hexagonal which is drilled and internally threaded. b. Washers are a perforated piece of metal, leather, or other materials of various shapes or forms. They are used primarily as a seat for a bolt, nut, screw, rivet or nail to distribute pressure and prevent endwise motion over an area and/or to prevent movement between two parts by means of restraining features created by the shape of its edges.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5315', '53', 'Nails, Machine Keys, and Pins', NULLIF('Includes Dowel Pins, Split Pins; Spikes; Tacks; Nonoffice Type Staples; Machine Keys; Brads; Shouldered Pins; Cotter Pins; Nails.', ''), NULLIF('Excludes Shafts (FSC 3040); Lock Keys (FSC 5340).', ''), NULLIF('Note-Nails, machine keys, and pins, including specially designed, will be classified in this FSC. a. Nails are slender, usually pointed fasteners with a head, and designed for insertion by impact. b. Machine keys are items generally inserted between two items, usually in a groove, to prevent relative rotation e.g., shaft and hub. c. Pins are cylindrical items of various materials used to align/attach two or more parts together and may allow for angular movement.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5320', '53', 'Rivets', NULLIF('', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5325', '53', 'Fastening Devices', NULLIF('Includes Eyelets; Grommets; Aircraft Cowling Fasteners; Textile Fasteners; Retaining Rings; Threaded Inserts.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5330', '53', 'Packing and Gasket Materials', NULLIF('Includes General Purpose Oil Grease, Air, Liquid, Gas, and chemical Seals; Oakum; Prefabricated Gaskets and Seals designed for a single specific application.', ''), NULLIF('Excludes O-Rings (FSC 5331); Bulk material items, such as; Rubber, Cork, and Asbestos Sheets and Strips (FSG 93); Liquid Gasket Materials (RTV, Permatex, etc.) (FSG 80); RFI and EMI Seals and Gaskets (FSC 5999), and all Seal Retainers.', ''), NULLIF('Note-This FSC includes only seals and gaskets designed for standard use. Seals, gaskets and packing are made from materials specifically designed and constructed to resist deterioration from the action of heat, gas, chemicals, liquids, and used to fill a cavity and create a tight seal. Similar materials used to make seals, gaskets, and packing are available in bulk form, refer to FSG 93, FSG 80, and FSG 83.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5331', '53', 'O-Ring', NULLIF('', ''), NULLIF('Excludes Packing and Gasket Material (FSC 5330).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5335', '53', 'Metal Screening', NULLIF('Includes Insect Screening; Industrial Metal Cloth; Industrial Metal Mesh.', ''), NULLIF('Excludes Fencing (FSC 5660).', ''), NULLIF('Note-Metal Screening is a perforated plate or cylinder or a meshed wire fabric usually mounted and used to separate coarse from finer parts.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5340', '53', 'Hardware, Commercial', NULLIF('Includes Access Covers; Bumpers; Casters; Cabinet and Door Hardware; Clevises; Hinges; Latches; Straps and Strapping; Turnbuckles; Webbed Straps.', ''), NULLIF('Excludes Weapons System Hardware (FSC 5342).', ''), NULLIF('Note-Common hardware designed primarily for commercial use will be classified in this FSC. Hardware is the physical, tangible and permanent components of end items that may not be classified elsewhere. It consists also of common items that are commonly used in construction and/or manufacturing (e.g., hinges, door locks).', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5341', '53', 'Brackets', NULLIF('Includes Angle Brackets; Double Angle Brackets; Mounting Brackets.', ''), NULLIF('Excludes Corner Braces (FSC 5340); "T" Braces (FSC 5340); Mending Plates (FSC 5340).', ''), NULLIF('FSC 5341 CANCELLED ON 030608 Brackets', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5342', '53', 'Hardware, Weapon System', NULLIF('Includes Adapters; Anchor Plates and Straps; Anodes; Bellows; Couplings; Control Rods; Access Doors; Fairleads; Mounts; Tie Rods; Yokes.', ''), NULLIF('Excludes Commercial Hardware (FSC 5340).', ''), NULLIF('Note-Hardware designed for weapon systems unique applications will be classified in this FSC.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5345', '53', 'Disks and Stones, Abrasive', NULLIF('Includes Abrasive Belts and Belting; Hones; Abrasive Wheels, Multiapplication.', ''), NULLIF('Excludes Dental Abrasives; Abrasive Cones, and other Abrasive Attachments for equipment.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5350', '53', 'Abrasive Materials', NULLIF('Includes Cloth; Papers; Powders; Abrasive Polishing Compounds; Metal Finishing Abrasives; Industrial Diamonds; Diamond Dust; Rouge.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5355', '53', 'Knobs and Pointers', NULLIF('Includes Knobs, including Calibrated Knobs; Dials, Scale.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5360', '53', 'Coil, Flat, Leaf, and Wire Springs', NULLIF('Includes Compression Springs; Torsion Springs; Leaf Spring Assemblies; Flat Springs; Extension Springs.', ''), NULLIF('', ''), NULLIF('Note-All springs, including specially designed, will be classified in this FSC. A spring is an elastic mechanical device that regains its original shape after being compressed, extended or deflected.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5365', '53', 'Bushings, Rings, Shims, and Spacers', NULLIF('Includes Externally Threaded Rings; Keyed and Serrated Lock Rings; Dee Rings; Instrument Retaining Rings; Shim Sets and Assortments; Plate Spacers; Ring Spacers; Sleeve Spacers; Stepped Spacers; Spacer Sets and Assortments; Sleeve Bushings; Machine Threaded Bushings; Machine Threaded Plugs.', ''), NULLIF('Excludes Sleeve Bearings (FSC 3120); Pipe Threaded Plugs (FSC 4730); Piston Rings (FSC 2520, 2805, 2810, 2815, 2825, 2990, 3040, 4310, and 4820); Retaining Rings (FSC 5325); Shim Stock (FSC 9515).', ''), NULLIF('Note-All items, including specially designed, will be  classified in this FSC. a. A bushing is cylindrical in shape, hollow, and designed primarily to be inserted into a hole to reduce the effective inside diameter of the hole, and to protect the body structure about the hole from damage resulting from stress, strain, and vibration. b. A ring is cylindrical in shape, usually flat with a center hole. Rings are multi-applicational. See specific item name for designated usage. c. A shim is a thin, sometimes tapered, piece of material used to fill in space between things. See specific item name for dimensional parameters. d. A spacer is a device for holding two members at a given distance from each other.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5410', '54', 'Prefabricated and Portable Buildings', NULLIF('Includes Prefabricated Panels; Inflatable Shelters; Metal Screens and Metal Windows.', ''), NULLIF('', ''), NULLIF('', ''));
