INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5836', '58', 'Video Recording and Reproducing Equipment', NULLIF('', ''), NULLIF('', ''), NULLIF('Note - This class includes and is restricted to electronic video equipment such as video monitors, televisions, cameras, printers, and recorders/reproducers used with miscellaneous communication equipment. Also included is associated equipment such as cleaners, splicers, rewinders, reels and tapes designed for use with video recording-reproducing equipment. Excluded from the class is photographic type equipment (Group 67), ADP type computer monitors (Group 70), and video equipment which includes radio receivers/transmitters (properly classified in more specific classes in Group 58). When cameras provide day and night video, they are to be classified in FSC 5836.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5840', '58', 'Radar Equipment, Except Airborne', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Radar assemblies and subassemblies designed specifically for use with fire control equipment or guided missiles are excluded from this class and are included in the appropriate classes of Group 12 or Group 14.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5841', '58', 'Radar Equipment, Airborne', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Radar assemblies and subassemblies designed specifically for use with fire control equipment or guided missiles are excluded from this class and are included in the appropriate classes of Group 12 or Group 14.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5845', '58', 'Underwater Sound Equipment', NULLIF('Includes Sonar Equipment; Underwater Listening Equipment; Sonobuoys; Sonobuoy Launchers; Fathometers; Sofar Equipment.', ''), NULLIF('', ''), NULLIF('Note-This class excludes items specifically designed for fire control sonar systems.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5850', '58', 'Visible and Invisible Light Communication Equipment', NULLIF('Includes Heliographs.', ''), NULLIF('', ''), NULLIF('Note-This class includes only communication types of infrared equipment. Excluded from this class are infrared assemblies, subassemblies, and components designed specifically for use in fire control systems, guided missile systems, test and maintenance specialized equipment, night vision equipment, and other noncommunication types of equipment.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5855', '58', 'Night Vision Equipment, Emitted and Reflected Radiation', NULLIF('Includes Sighting and Observation Devices, Target Detectors and Locators, Remote Viewing Systems utilizing heat radiation emitted from object(s), associated Radiation Sources, Image Converters and Image Intensifiers, Optical Cells and other components designed for Night Vision Equipment.  Holders and Lens used with Infrared Equipment are also included in this FSC.', ''), NULLIF('Excludes Optical Sighting and observation devices which incorporate both day and night viewing characteristics within an integral unit, nonnight vision type items designed for use with guided missiles, communications, or laboratory equipment, and Optical Elements, such as Lens, Prisms, Windows.', ''), NULLIF('Note-This class includes image forming devices and associated aids, their subassemblies and components, utilizing passive and/or active techniques for detection, observation, and identification of terrain and objects at night, while maintaining concealment afforded by darkness. Includes items having multiapplication for purposes of fire control, and/or night observation, and/or detection. Excluded from this class are items for which more specific classifications are suitable as prescribed by the structure and indexes of the FSC.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5860', '58', 'Stimulated Coherent Radiation Devices, Components, and Accessories', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes, and is restricted to, devices the operation of which is based on principles involving the stimulated emission of radiation, and to associated components and accessories directly related to stimulated coherent radiation techniques. The methods for producing the radiation are those obtained by LASER (Light Amplification by Stimulated Emission of Radiation) and by MASER (Microwave and/or Molecular Amplification by Stimulated Emission of Radiation) techniques. Only those items specifically designed for stimulated coherent radiation applications, and having no other application, are to be included in this class. Assemblies or major systems containing such devices are excluded from this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5865', '58', 'Electronic Countermeasures, Counter-Countermeasures and Quick Reaction Capability Equipment', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes, and is restricted to, passive and active electronic equipment, systems, and subsystems designed to prevent or reduce an enemy''s effective use of radiated electromagnetic energy or designed to insure our own effective use of radiated electromagnetic energy. Includes Electronic Countermeasures, Electronic Counter-Countermeasures, Electronic Support Measures, and Quick Reaction Capability Equipment and components specially designed therefore which are not classifiable elsewhere in the FSC Structure. Excluded from this class are non-electronic items which are properly classified in more specific classes in accordance with the FSC structure and indexes.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5895', '58', 'Miscellaneous Communication Equipment', NULLIF('Includes Modems.', ''), NULLIF('Excludes Electronic Countermeasures Equipment.', ''), NULLIF('Note-This class includes miscellaneous communication equipment that is not classified in more specific classes (see the NOTE for FSG 58).', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5905', '59', 'Resistor', NULLIF('Includes Varistors; Resistive ballast Tubes; Rheostats; Resistor Networks; Resistor Mounting Hardware; Thermistors./', ''), NULLIF('Excludes Resistance Wire./', ''), NULLIF('', ''));
