INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5915', '59', 'Filters and Networks', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes those filters and networks which consist of a combination of resistors, capacitors, or coils, but not a combination of resistors only (classified in 5905), capacitors only (classified in 5910), or coils only (classified in 5950).', ''));
