INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('5955', '59', 'Oscillators and Piezoelectric Crystals', NULLIF('Includes Crystal Units, Processed Unmounted Crystals, Crystal Controlled Oscillators and Noncrystal Controlled Oscillators', ''), NULLIF('Excludes Crystal Controlled Oscillators and Noncrystal Controlled Oscillators which meet the definition of the term "electronic module" as defined for FSC 5963 and Microcircuits', ''), NULLIF('', ''));
