INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6020', '60', 'Fiber Optic Cable Assemblies and Harnesses', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are items used for communication and/or data transmission having one or more fiber optic conductors/cables with processed end(s) or terminated in fitting(s). Excluded from this class are items used for illumination and/or image transmission.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6021', '60', 'Fiber Optic Switches', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are switches used for line selection in fiber optic systems.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6025', '60', 'Fiber Optic Transmitters', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6025 CANCELLED ON 050395 Fiber Optic Transmitters', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6026', '60', 'Fiber Optic Receivers', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6026 CANCELLED ON 050395 Fiber Optic Receivers', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6029', '60', 'Optical Repeaters', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6029 CANCELLED ON 050395 Optical Repeaters', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6030', '60', 'Fiber Optic Devices', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are such items as transmitters, receivers, multiplexers, demultiplexers, modulators, demodulators, repeaters, attenuators, filters, integrated optical ciruuits, and the like, used in fiber optic systems.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6031', '60', 'Integrated Optical Circuits', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6031 CANCELLED ON 050395 Integrated Optical Circuits', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6032', '60', 'Fiber Optic Light Sources and Photo Detectors', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are light emitting and laser diodes and photo detectors used in fiber optic systems.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6033', '60', 'Fiber Optic Photo Detectors', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6033 CANCELLED ON 050395 Fiber Optic Photo Detectors', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6034', '60', 'Fiber Optic Modulators/Demodulators', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6034 CANCELLED ON 050395 Fiber Optic Modulators/Demodulators', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6035', '60', 'Fiber Optic Light Transfer and Image Transfer Devices', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are fiber optic devices used for illumination and/or image transmission. Excluded from this class are items used for communication and/or data transmission.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6040', '60', 'Fiber Optic Sensors', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6040 CANCELLED ON 050395 Fiber Optic Sensors', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6050', '60', 'Fiber Optic Passive Devices', NULLIF('', ''), NULLIF('', ''), NULLIF('FSC 6050 CANCELLED ON 050395 Fiber Optic Passive Devices', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6060', '60', 'Fiber Optic Interconnectors', NULLIF('', ''), NULLIF('Excludes electrical connectors', ''), NULLIF('Note-Included in this class are all types of fiber optic terminations, such as connectors, dividers, couplers, rotary and nonrotary joints, splitters, mixers and splicers and the like.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6070', '60', 'Fiber Optic Accessories and Supplies', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are mode stripping materials, index matching materials and other related supplies. Excluded from this class are hardware and supplies common to both electrical and fiber optic applications which are classifiable on FSC 5975.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6080', '60', 'Fiber Optic Kits and Sets', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are kits and sets which consist of fiber optic components only; fiber optic components and related parts; fiber optic components and related tools; or fiber optic components, related parts and related tools. Excluded from this class are tool kits and sets which are classifiable in FSC 5180; test sets/instruments classifiable in FSG 66; and end item sets/equipment utilizing fiber optic components which are classifiable in the applicable equipment FSC.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6099', '60', 'Miscellaneous Fiber Optic Components', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-Included in this class are fiber optic items which are not classifiable in a more specific class. (See the "Note" for FSG 60.)', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6105', '61', 'Motors, Electrical', NULLIF('', ''), NULLIF('Excludes Starting Motors for Engines.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6110', '61', 'Electrical Control Equipment', NULLIF('Includes Contactors; Motor Controllers; Power Servomechanisms; Switchgear; Voltage Regulators.', ''), NULLIF('Excludes Automatic Pilot Mechanisms; Relays; Resistors.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6115', '61', 'Generators and Generator Sets, Electrical', NULLIF('Includes Engine, Turbine, Wind, and Hand Driven Generator Sets and Auxiliary Aircraft Generators.', ''), NULLIF('Excludes Motor-Converters; Motor-Generator Sets; Engine Generators; Engine Accessory Generators.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6116', '61', 'Fuel Cell Power Units, Components, and Accessories', NULLIF('Includes Complete Functioning Fuel Cell Power Units; Fuel Cells; Fuel Cell Assemblies or Modules; Fuel Cell Reactant Chambers; Fuel Feed Mechanisms; Electrolyte Heaters; Heat Exchangers; and other internal components peculiar to, and used exclusively on or with, Fuel Cell Power Units classified in this class.', ''), NULLIF('Excludes Primary Batteries (FSC 6135); Secondary Batteries (FSC 6140); Generators and Generating Sets (FSC 6115); and items for which more suitable classes are specifically prescribed by the indexes and structure of the FSC.', ''), NULLIF('Note-This class includes items that are designed to produce an electromotive force directly from an oxidation-reduction type of chemical reaction. A complete power unit must include tanks, pumps, and associated control equipment, and requires a continuous, uninterrupted supply of liquid or gaseous fuel and oxidizers.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6117', '61', 'Solar Electric Power Systems', NULLIF('', ''), NULLIF('', ''), NULLIF('Note-This class includes items which are designed for the direct conversion of solar energy into electrical power.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6120', '61', 'Transformers: Distribution and Power Station', NULLIF('', ''), NULLIF('Excludes Potential and Current Transformers', ''), NULLIF('Note-This class includes transformers with a kilovolt-ampere rating above 1 kva.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6125', '61', 'Converters, Electrical, Rotating', NULLIF('Includes Complete Battery Charging Equipment, Rotating; Dynamotors; Motor-Converters; Motor-Generator Sets; Phase Converters; Rotating Equipment; Synchronous Converters.', ''), NULLIF('Excludes Nonrotating Equipment.', ''), NULLIF('Note-This class includes devices employing mechanical rotation for changing electrical energy from one form to another (i.e., AC to AC, DC to DC, AC to DC, and DC to AC). Excluded from this class are electrical generators and generator sets (FSC 6115).', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6130', '61', 'Converters, Electrical, Nonrotating', NULLIF('Includes Complete Battery Charging Equipment, Nonrotating; Power Supplies, Multiapplication.', ''), NULLIF('Excludes Rectifying Tubes; (FSC 5960); Rectifying Crystals (FSC 5961); Rotating Equipment (FSC 6125); Transformers (FSC 5950); and Semiconductor Devices and Associated Hardware (FSC 5961).', ''), NULLIF('Note-This class includes devices employing a means other than mechanical rotation for changing electrical energy from one form to another (i.e., AC to AC, DC to DC, AC to DC, and DC to AC).', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6135', '61', 'Batteries, Nonrechargeable', NULLIF('Includes Cells and Dry Batteries; Nuclear Batteries.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6140', '61', 'Batteries, Rechargeable', NULLIF('Includes Rechargeable Cells and Batteries.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6145', '61', 'Wire and Cable, Electrical', NULLIF('Includes Insulated and Noninsulated, Wire and Cable, such as, Antenna Wire, Coaxial Cable, Fuse and Resistance Wire, and Magnet Wire.', ''), NULLIF('Excludes Radio Frequency Transmission Line (FSC 5985); Fiber Optic Cable (FSC 6015); Communication Cable Assemblies (FSC 5995); Distribution and Power Transmission Cable Assemblies (FSC 6150).', ''), NULLIF('Note-This class includes only bulk electrical wire and cable. Excluded from this class are electrical wire and cable items of definite length either with processed ends, or terminated in fittings.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6150', '61', 'Miscellaneous Electric Power and Distribution Equipment', NULLIF('Includes Appliance and Extension Cords; Electric Power and Distribution Cable with Attachments, Multiapplication; Common Components of Electrical Rotating Equipment, such as End Bells and Frames.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6160', '61', 'Miscellaneous Battery Retaining Fixtures, Liners and Ancillary Items', NULLIF('Includes Battery Boxes, Covers, Liners, Racks, Retainers, and Trays.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6210', '62', 'Indoor and Outdoor Electric Lighting Fixtures', NULLIF('Includes Airport, Railroad Platform, Stadium, and Street Lighting Fixtures.', ''), NULLIF('Excludes Nonelectrical Outdoor Fixtures; Signal Fixtures; Nonelectrical Indoor Fixtures.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6220', '62', 'Electric Vehicular Lights and Fixtures', NULLIF('Includes Automotive, Marine, Railroad, and Aircraft Lights and Fixtures.', ''), NULLIF('Excludes Nonelectrical Vehicular Fixtures.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6230', '62', 'Electric Portable and Hand Lighting Equipment', NULLIF('Includes Floodlights; Searchlights; Extension Lights.', ''), NULLIF('Excludes Nonelectrical Portable and Hand Lighting Equipment; Signal and Alarm Equipment.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6240', '62', 'Electric Lamps', NULLIF('Includes Fluorescent Lamps; Incandescent Lamps, Large and Miniature; Light Emitting Diode Lamps; Mercury Lamps; Sodium Lamps.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6250', '62', 'Ballasts, Lampholders, and Starters', NULLIF('', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6260', '62', 'Nonelectrical Lighting Fixtures', NULLIF('Includes Lanterns, Nonelectrical; Hand and Portable Carbide Lamps; Candles.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6310', '63', 'Traffic and Transit Signal Systems', NULLIF('', ''), NULLIF('Excludes Railroad Signals.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6320', '63', 'Shipboard Alarm and Signal Systems', NULLIF('', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6330', '63', 'Railroad Signal and Warning Devices', NULLIF('Includes Railroad Gates; Flasher Lights; Block Signals.', ''), NULLIF('Excludes Semaphore Flag Signals.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6340', '63', 'Aircraft Alarm and Signal Systems', NULLIF('Includes Oxygen Pressure Signals and Warning Devices; such as Air Pressure Warning Signals, Aircraft Crew Warning Signals, Altitude Warning Signals, Alarm Controls, Audible Landing Gear Alarms.', ''), NULLIF('Excludes Aircraft Engine Oil and Fuel Warning Devices.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6350', '63', 'Miscellaneous Alarm, Signal, and Security Detection Systems', NULLIF('Includes Anti-intrusion Alarm Systems; Foghorns; Gongs; Chimes; Bells; Burglar Alarm Systems; Fire Alarms; Police Alarm Systems; Sounding Devices; Manual Gas Alarms; Landing Wands; Security Inspection Systems', ''), NULLIF('Excludes Loran; Radar; Sonar; Engine Oil and Fuel Warning Devices; Aircraft Oxygen Warning Devices.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6505', '65', 'Drugs and Biologicals', NULLIF('Includes all drugs, medicinal grade chemicals, and Biologicals subject to the Federal Food, Drug and Cosmetic Act, listed in the Monograph section of the U.S. Pharmacopeia or the National Formulary or covered by the United States Adopted Names (USAN). Other nations may apply their own legal documents.', ''), NULLIF('Excludes medicated cosmetics and toiletries classifiable in FSC 6508 and in vitro diagnostic substances and reagents classifiable in FSC 6550, and Veterinary Drugs and Biologicals classifiable in FSC 6509.', ''), NULLIF('Note-Only items specifically formulated for human use are appropriate to this class.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6508', '65', 'Medicated Cosmetics and Toiletries', NULLIF('Includes medicated cosmetics and toiletries that have been determined to be drugs in accordance with the Federal Food, Drug, and Cosmetic Act.', ''), NULLIF('Excludes nonmedicated cosmetics and toiletries which are classifiable in Group 85.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6509', '65', 'Drugs and Biologicals, Veterinary Use', NULLIF('Includes All Drugs, medicinal grade chemical, and Biologicals subject to the provisions of the Department of Agriculture (Veterinary Drugs and Biologicals) specifically formulated for Veterinary use. Other Nations may apply their own legal documents.', ''), NULLIF('Excludes Drugs and Biologicals specifically formulated for Human use classifiable in FSC 6505.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6510', '65', 'Surgical Dressing Materials', NULLIF('Includes Bandages, Compresses, Dressings, Gauze, Pads, Sponges, and impregnated surgical dressing materials.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6515', '65', 'Medical and Surgical Instruments, Equipment, and Supplies', NULLIF('Includes: Anesthesia Apparatus; Blood Transfusion Apparatus; Oxygen Therapy Apparatus; Respirators; Orthopedic Supplies; Arch Supports; Clinical Thermometers; Sutures; Hearing Aids; Veterinary Equipment; Endoscopes, Fiber Optic; and In Vitro Diagnostic Devices classified and regulated by the FDA.', ''), NULLIF('Excludes: Ophthalmic Instruments, Equipment, and Supplies; Non-Medical Endoscopes; In Vitro Diagnostic Substances, Reagents, Test Kits and Sets (6550).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6520', '65', 'Dental Instruments, Equipment, and Supplies', NULLIF('Includes Dental Engines; Dental Laboratory Equipment; Operating Chairs; Orthodontic Appliances; Teeth; Dental Metals; Dental Abrasives; Dental Laboratory Furniture.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6525', '65', 'Imaging Equipment and Supplies: Medical, Dental, Veterinary', NULLIF('Includes Medical X-ray Film; Medical X-Ray Film Viewing Equipment; Medical X-Ray Film Processing and Finishing Equipment and Supplies; X-Ray Tubes; Computerized Tomography (CT) scanners and related equipment; Magnetic Resonance Imaging machine and related equipment; Positron Emission Tomography (PET) scanners and related equipment; Computed and/or Direct Digital Radiography machines and related equipment; Ultrasound and Echocardiogram scanners and related equipment; Scintigraphy equipment; Medical Picture Archiving and Communication Systems (PACS) equipment; Medical Thermal Imaging equipment; Optical Coherence Tomography (OCT) spectroscopy machines and related equipment; and imaging supplies and Contrast delivery systems that are not used by other medical disciplines.', ''), NULLIF('Excludes Industrial imaging equipment (X-ray, Thermal and the rest;) Supplies and equipment that can be used by other medical disciplines (See Medical and Surgical Instruments, Equipment and Supplies and Hospital and Surgical Clothing and related Special Purpose Items.)', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6530', '65', 'Hospital Furniture, Equipment, Utensils, and Supplies', NULLIF('Includes Orthopedic Equipment; Operating Lights; Physiotherapy Equipment; Sterilizers; WheelChairs; Litters; Hospital Beds; Restraint Equipment.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6532', '65', 'Hospital and Surgical Clothing and Related Special Purpose Items', NULLIF('Includes Only those clothing items which are required exclusively for use in hospital operations, or in medical or dental facilities, which have no functional counterpart elsewhere in Federal Organizations participating in the Federal Catalog System.', ''), NULLIF('Excludes General purpose items of clothing and fabricated textile material.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6540', '65', 'Ophthalmic Instruments, Equipment, and Supplies', NULLIF('Includes Ophthalmic Lens Cutting and Grinding Machine; Ophthalmic Lenses and Lens Blanks; Spectacle Frames; Ophthalmological and Optometric Instruments, Equipment, and Supplies.', ''), NULLIF('Excludes Optical Lens Cutting and Grinding Machines: Ophthalmoscope and Otoscope Set; Surgical Instruments and Supplies that can be used by other medical disciplines; i.e., sutures, eye pads, eye shields, eye bath, and the like.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6545', '65', 'Replenishable Field Medical Sets, Kits, and Outfits', NULLIF('Includes Sets and kits which can be replenishable after use such as combat lifesaver kit (CLS), first aid kits, and tactical combat medical care (TCMC) kits, operational and contingency sets.', ''), NULLIF('Excludes Sets and kits which can be logically classified in another FSC Class in Group 65, such as Insect Sting Kit (FSC 6505); toscope and Ophthalmoscope Set (FSC 6540); In Vitro Diagnostic Test Kits (Fsc 6550); and the like.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6550', '65', 'In Vitro Diagnostic Substances, Reagents, Test Kits and Sets', NULLIF('Includes In vitro diagnostic aids, such as test tablets, test slides, test strips, test cards, test papers, test solutions, reagents; sets and kits designated for in vitro qualitative and quantitative tests on specimens, such as blood, urine, feces, and the like, for the purposes of aiding in the medical diagnosis of disease, determining body organ dysfunction, or other abnormal conditions.', ''), NULLIF('Excludes In vivo diagnostic substances classifiable in FSC 6505.', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6605', '66', 'Navigational Instruments', NULLIF('Includes Azimuths; Sextants; Octants; Compasses; Plotting Boards; Underwater Log Equipment; Air Position Indicators; Drift Meters.', ''), NULLIF('', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6610', '66', 'Flight Instruments', NULLIF('Includes Air Speed Indicators; Rate of Climb Indicators; Bank and Turn Indicators; Pitot Tubes; Gyro Horizon Indicators; Attitude Gyro Indicators.', ''), NULLIF('Excludes Navigational Instruments (FSC 6605).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6615', '66', 'Automatic Pilot Mechanisms and Airborne Gyro Components', NULLIF('Includes Automatic Pilot Regulators; Directional, Vertical, Bank and Turn, and Hydraulic Surface Gyro Controls; Airborne and Shipborne Automatic Pilot Mechanisms; Helicopter Automatic Stabilization Equipment.', ''), NULLIF('Excludes Automatic Pilot Training Devices (FSC 6930); and Automatic Pilot Mechanisms, Guided Missile (FSC 1420).', ''), NULLIF('Note-Included in this class are gyro components of guided missiles. Excluded are complete gyro mechanisms and nonairborne gyro components, both of which are classified in the same classes as their next higher assemblies.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6620', '66', 'Engine Instruments', NULLIF('Includes All Engine Instruments, including Aircraft, Marine, and Vehicular; Fuel Pressure Gages; Manifold Pressure Gages; Oil Pressure Gages; Fuel Mixture Indicators; Engine Oil and Fuel Warning Devices.', ''), NULLIF('', ''), NULLIF('Note-Instruments designed for use on both engines and other than engines are not included in this class and should be classified in the appropriate instruments class of Group 66.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6625', '66', 'Electrical and Electronic Properties Measuring and Testing Instruments', NULLIF('Includes Test Leads and Test Lead Attachments; Test instruments designed for communication equipment; Test instruments designed for use with electronic equipment classified in two or more FSC groups.', ''), NULLIF('Excludes Radiac Equipment', ''), NULLIF('Note-This class includes all "basic" types of electrical and electronic test instruments, such as ammeters, voltmeters, ohmmeters, multimeters, and similar instruments, even if specially designed. Excluded from this class by the modifier "except specially designed" are test instruments, other than "basic" instruments, which are specially designed for use with atomic ordnance, fire control, guided missile, motor vehicle, aircraft, or photographic equipment. Such specially designed test instruments are to be classified in other classes in the Federal Supply Classification.  This class includes communications test equipment, infrared test equipment designed for communication, and night vision maintenance and test specialized equipment.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6630', '66', 'Chemical Analysis Instruments', NULLIF('ncludes: Acidity (pH) Meters; Gas Analyzers; Alkalinity Measuring Instruments; Colorimeters.', ''), NULLIF('Excludes: Gas Detecting Equipment; Manual Gas Alarms; Chemicals; In Vitro Diagnostic Substances and Reagents; Analysis Instruments Intended for Medical Laboratory Use.', ''), NULLIF('Note: This class is used for all non-medical scientific analyzers and instrumentation used specifically for industrial or scientific applications. For all analyzers and instrumentation used in a medical/clinical setting for the purpose of aiding in the medical diagnosis of disease see FSC 6632.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6632', '66', 'Medical Laboratory Analysis Instruments', NULLIF('Includes: Instrumentation performing the following analysis: Immunochemistry, Clinical Chemistry, Coagulation, Hematology, Clinical Microbiology, Urinalysis, Differential Counters, Histology, Cytology Equipment, and Point of Care Analyzers and Related Supporting Components and Parts are also Included in this FSC.', ''), NULLIF('Excludes: Non-medical use instrumentation. Gas detecting equipment, manual gas alarms, air quality monitoring equipment, chemicals, In Vitro Diagnostic Devices, In Vitro Diagnostics Substances, Reagents, water monitors, water quality instruments, general use meters, stack emission measuring equipment, Ph and ion-selective meters, titrators and titration systems, and all other nonmedical analysis instrumentation. Also excludes general use laboratory equipment and supplies such as beakers, flasks, test tubes, balances, scales. Note: For clinical laboratory reagents see FSC Code 6550. For analysis instruments intended for non-medical labratory use, see FSC 6630.', ''), NULLIF('Note: This class is used for all medical/clinical setting analyzers and instrumentation used for the purpose of aiding in the medical diagnosis of disease. For all non - medical CHEMICAL scientific analyzers and instrumentation used specifically for industrial or scientific applications see FSC 6630.', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6635', '66', 'Physical Properties Testing and Inspection', NULLIF('Includes Destructive and Nondestructive Inspection Equipment such as Fluorescent Penetrant Inspection Units; Magnetic Inspection Units; Industrial X-Ray Machines; Industrial X-Ray Film; Tensiometers; Material Hardness Testers.', ''), NULLIF('Excludes General Purpose Measuring Tools (FSC 5210) and Precision Measuring Tools and Inspection Gages (FSC 5220).', ''), NULLIF('', ''));
INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('6636', '66', 'Environmental Chambers and Related Equipment', NULLIF('Includes Chambers; Wind Tunnels; Enclosures specifically designed to simulate an environmental condition; Weatherometers; Fadeometers; Specially designed equipment and components exclusively related to environmental chambers.', ''), NULLIF('Excludes General purpose prefabricated buildings, chambers, panels, and components (FSC 5410); Controlled Environmental Conditioning Kits and Sets (FSC 4240); Baths (FSC 4510); Ovens (FSC 7310); Kilns (FSC 3630); Radiosonde Baseline Check Sets (FSC 6660); Divers Recompression Chambers (FSC 4220); Altitude Training Decompression Chambers (FSC 6930); and Chambers used as training devices (FSC 6930); Checkout and Test Equipment (FSC 6625); Chemical Analysis Instruments (FSC 6630); Electrical and Electronic Properties Measuring and Testing Instruments (FSC 6625); and Meteorological Instruments (FSC 6660).', ''), NULLIF('Note-This class includes only those environmental chambers used exclusively to perform controlled tests, under simulated conditions, to determine physical damage and/or changes in operating characteristics for equipments and components such as Humidity; Immersion; Moisture; Altitude; Explosion; Thermal Shock; Sand and Dust; Flammability; Seal. Environmental chambers included in this class provide data regarding service life and behavior of various items equivalent to actual service conditions existing in the field.', ''));
