INSERT INTO fsc (fsc_code, fsg_code, fsc_title, fsc_includes, fsc_excludes, fsc_notes) VALUES ('8145', '81', 'Specialized Shipping and Storage Containers', NULLIF('Includes Specially designed components (not elsewhere classifiable) peculiar to special shipping and storage containers as delimited under this class.', ''), NULLIF('Excludes General purpose containers; specially designed containers for ammunition, nuclear ordnance, explosives, military chemical agents and guided missile components. Excluded from this class are items for which more specific classifications are suitable. The FSC Indexes and Structure will govern the classification of those items permitted classification in a single class only.', ''), NULLIF('Note-This class includes only reusable and repairable containers specially designed for shipping and storage of specialized equipment; i.e., shipping and storage containers for components of aircraft, space vehicles, automotive vehicles, ships, ground communication equipment, etc.', ''));
